(function($){$('[data-behavior~="meganav-content"]').hide();var $content=$('[data-behavior~="meganav-content"]');$content.addClass("js-meganav-content");var $openMenus;var $closeBar=$('<a class="close-bar" title="Close menu"><span></span></a>');$closeBar.addClass("shadow12-up");$closeBar.click(function(e){$openMenus=$('[data-behavior~="meganav-content"].is-open');closeAll();});$('[data-behavior~="meganav"] a').click(function(e){e.preventDefault();var menu=$(this).attr("data-menu");var $target=$(this);$openMenus=$('[data-behavior~="meganav-content"].is-open');if($(this).hasClass("is-current")){closeAll();}else{if($openMenus.length===0){$(menu).append($closeBar).slideDown().addClass("is-open");$target.addClass("is-current");}else{$openMenus.hide();$('[data-behavior~="meganav"] .is-current').removeClass("is-current");$(menu).show().append($closeBar).addClass("is-open");$target.addClass("is-current");}}});closeAll=function(callback){$('[data-behavior~="meganav"] .is-current').removeClass("is-current");if(callback){if($openMenus.length>0){$openMenus.slideUp(function(){if($('[data-behavior~="meganav-content"].is-open:animated').length===0){callback();}}).removeClass("is-open");}else{callback();}}else{if($openMenus.length>0){$openMenus.slideUp().removeClass("is-open");}}};})(jQuery);