      iNavCreateCookie: function(iNavName,iNavValue,iNavDays) {
        if (iNavDays) {
          var iNavDate = new Date();
          iNavDate.setTime(iNavDate.getTime()+(iNavDays*24*60*60*1000));
          var iNavExpires = "; iNavExpires="+iNavDate.toGMTString();
        }
        else { var iNavExpires = ""; }
        var iNavDomain = iNavNGI.iNavGetDomain(); 
        if (iNavDomain) { document.cookie = iNavName+"="+ iNavValue + iNavExpires + "; path=/; domain="+iNavDomain; }
        else { document.cookie = iNavName+"="+ iNavValue + iNavExpires + "; path=/"; }
      },
      iNavReadCookie: function(iNavName) {
        var iNavNameEQ = iNavName + "=";
        var iNavCa = document.cookie.split(';');
        for(var i=0;i < iNavCa.length;i++) {
          var iNavC = iNavCa[i];
          while (iNavC.charAt(0)==' ') 
            iNavC = iNavC.substring(1,iNavC.length);
            if (iNavC.indexOf(iNavNameEQ) == 0) { return iNavC.substring(iNavNameEQ.length,iNavC.length); }
        }
        return null;
      },
      iNavEraseCookie: function(iNavName) { createCookie(iNavName,"",-1); },
      iNavGetDomain: function() {
        var iNavTmpDomain = document.domain; 
        var iNavPairs = iNavTmpDomain.split("."); 
        var j = iNavPairs.length; 
        if (parseInt(j) == 0) { var iNavCurDomain = null; }
        else if (parseInt(j) == 1) { var iNavCurDomain = iNavPairs[0]; }
        else if (parseInt(j) == 2) { var iNavCurDomain = iNavPairs[0] + "." + iNavPairs[1]; }
        else { var iNavCurDomain = "." + iNavPairs[parseInt(j)-2] + "." + iNavPairs[parseInt(j)-1]; }
        return iNavCurDomain; 
      },
