(function (jQuery) {
    jQuery.fn.supersized = function () {
        jQuery.inAnimation = false;
        jQuery.paused = false;
        jQuery.currentPosition = 1;
        jQuery.sonosopra = false;
        var options = jQuery.extend(jQuery.fn.supersized.defaults, jQuery.fn.supersized.options);
        
        //window.console.log(options.pause_hover);
        jQuery(window).bind("load", function () {
            jQuery('#web-supersize').fadeIn('fast');
            if (jQuery('#slideshow .activeslide').length == 0) jQuery('#web-supersize div:first').addClass('activeslide');
            if (options.slideshow == 1 && jQuery('#web-supersize div').length > 1 ) {
                if (options.slide_counter == 1) {
                    jQuery("#web-supersize > *").each(function (index) {
                        jQuery("#web-slidecounter").append('<img src="http://static.gateway.com/up/Static/shim.gif" class="bullet" position="' + index + '"/>')
                    });
                    jQuery('#web-slidecounter img').click(function () {
                        nextslide(jQuery(this).attr('position'))
                    });
                    jQuery('#web-slidecounter img:first').addClass('activesbullet')
                }
                slideshow_interval = setInterval("nextslide()", options.slide_interval)

                jQuery('#click-banner').hover(function () {
                    /*window.console.log(jQuery.currentPosition); 
                    nextslide(jQuery.currentPosition - 1)*/
                    jQuery.sonosopra = true;

                }, function () {
                    /*window.console.log(jQuery.currentPosition); 
                    nextslide(jQuery.currentPosition - 1)*/
                    jQuery.sonosopra = false;

                })
            }
        });
        jQuery(document).ready(function () {
            jQuery('#web-supersize').resizenow()
        });
        jQuery(window).bind("resize", function () {
            jQuery('#web-supersize').resizenow()
        });
        jQuery('#web-supersize').hide()

    };
    jQuery.fn.resizenow = function () {
        var options = jQuery.extend(jQuery.fn.supersized.defaults, jQuery.fn.supersized.options);
        return this.each(function () {
            var ratio = options.startheight / options.startwidth;
            var imagewidth = 2000//jQuery(this).width();
            var imageheight = 1250//jQuery(this).height();
            var browserwidth = jQuery(window).width();
            //alert(browserwidth)
            var browserheight = jQuery(window).height();
            var offset;
            /*
            if ((browserheight / browserwidth) > ratio) {
                jQuery(this).height(browserheight);
                jQuery(this).width(browserheight / ratio);
                jQuery(this).children().height(browserheight);
                jQuery(this).children().width(browserheight / ratio);
                jQuery(this).parent().height(browserheight)
            } else {
                jQuery(this).width(browserwidth);
                jQuery(this).height(browserwidth * ratio);
                jQuery(this).children().width(browserwidth);
                jQuery(this).children().height(browserwidth * ratio);
                jQuery(this).parent().height(browserheight)
            }*/

            jQuery(this).css('height', imageheight);
                jQuery(this).css('width', imagewidth);
                jQuery(this).children().css('height', imageheight);
                jQuery(this).children().css('width', imagewidth);
                //jQuery(this).parent().css('height', browserheight)
                jQuery(this).children().css('left', function () { return (browserwidth - imagewidth) / 2 + 'px'});
            	/*
             	jQuery(this).height(imageheight);
                jQuery(this).width(imagewidth);
                jQuery(this).children().height(imageheight);
                jQuery(this).children().width(imagewidth);
                jQuery(this).parent().height(imageheight)
                jQuery(this).children().css('left', function () { return (browserwidth - imagewidth) / 2 + 'px'});
*/
           
            if (options.vertical_center == 1) {
                jQuery(this).children().css('left', (browserwidth - imagewidth) / 2);
                //jQuery(this).children().css('top', (browserheight - jQuery(this).height()) / 2)
            }
            return false
        })
    };
    jQuery.fn.supersized.defaults = {
        startwidth: 4,
        startheight: 3,
        vertical_center: 1,
        slideshow: 1,
        navigation: 1,
        transition: 1,
        pause_hover: 1,
        slide_counter: 1,
        slide_captions: 1,
        slide_interval: 7000
    }
})(jQuery);

function nextslide(position) {

    if (jQuery.sonosopra) return false;

        
    if (jQuery.inAnimation) return false;
    else jQuery.inAnimation = true;
    if (position) {
        jQuery.currentPosition = position
        //window.console.log(position);
    }
    var options = jQuery.extend(jQuery.fn.supersized.defaults, jQuery.fn.supersized.options);
    var currentslide = jQuery('#web-supersize .activeslide');
    var currentbullet = jQuery('#web-slidecounter img.activesbullet');
    currentslide.removeClass('activeslide');
    currentbullet.removeClass('activesbullet');
    if (currentslide.length == 0) currentslide = jQuery('#web-supersize div:last');
    if (currentbullet.length == 0) currentbullet = jQuery('#web-slidecounter img:last');
    var nextslide = currentslide.parent().find('div:eq(' + jQuery.currentPosition + ')');
    nextslide = nextslide ? nextslide : jQuery('#web-supersize div:first');
    var nextbullet = currentbullet.parent().find('img:eq(' + jQuery.currentPosition + ')');
    nextbullet = nextbullet ? nextbullet : jQuery('#web-slidecounter img:first');

    if (options.slide_counter == 1) {
        nextbullet.addClass('activesbullet')
    }
    nextslide.addClass('activeslide');
    if (options.transition == 0) {
        jQuery.inAnimation = false
    }
    if (options.transition == 1) {
        nextslide.hide();
        nextslide.fadeIn(750, function () {
            jQuery.inAnimation = false
        })
    }
    if (options.transition == 2) {
        nextslide.hide();
        nextslide.show("slide", {
            direction: "up"
        }, 'slow', function () {
            jQuery.inAnimation = false
        })
    }
    if (options.transition == 3) {
        nextslide.hide();
        nextslide.show("slide", {
            direction: "right"
        }, 'slow', function () {
            jQuery.inAnimation = false
        })
    }
    if (options.transition == 4) {
        nextslide.hide();
        nextslide.show("slide", {
            direction: "down"
        }, 'slow', function () {
            jQuery.inAnimation = false
        })
    }
    if (options.transition == 5) {
        nextslide.hide();
        nextslide.show("slide", {
            direction: "left"
        }, 'slow', function () {
            jQuery.inAnimation = false
        })
    }

    /*if (options.pause_hover == 1) {
        nextslide(jQuery.currentPosition - 1)
        window.console.log(jQuery.currentPosition);

    }*/


    jQuery.currentPosition = parseInt(jQuery.currentPosition) + 1;
    if (jQuery.currentPosition >= jQuery("#web-supersize > *").size()) jQuery.currentPosition = 0;
    jQuery('#web-supersize').resizenow()
}