$(document).ready(function() {
	$('#piryx-donation-form input.required, #piryx-donation-form select.required').each(function(){
		var customclass = $(this).parent().parent().attr('class');
		$(this).addClass(customclass);
	});
	$('#edit-eligibility-fieldset-eligibility-confirm').addClass("validate[required]").addClass("required");
	$('#other-amount input').addClass("validate[custom[monetary]]");
	$("#piryx-donation-form").validationEngine('init', {promptPosition : "topRight"});
	$("#piryx-donation-form #edit-cc-info-expiration-month-month, #piryx-donation-form #edit-cc-info-expiration-year-year").change(function(){
		var sel = $(this).find('option:selected').text();
		if(sel==""){$(this).parent().parent().parent().find('label').show();}
		else{$(this).parent().parent().parent().find('label').hide();}
	});
	$('#piryx-donation-form a.info_popup_link').click(function(){$(this).parent().find('.info_popup').toggle();});
	$('#piryx-donation-form .info_popup').click(function(){$(this).hide();});
	$('#piryx-donation-form #edit-cc-info-reccuring').click(function(){
		if($('#piryx-donation-form #edit-cc-info-reccuring:checked').val() == 1){
			$('#piryx-donation-form #edit-cc-info-recurring-fieldset-TotalPayments').val('0');
			$('#piryx-donation-form #edit-cc-info-recurring-fieldset-BillingPeriod').val('Monthly');
		}
		else{
			$('#piryx-donation-form #edit-cc-info-recurring-fieldset-TotalPayments').val('');
			$('#piryx-donation-form #edit-cc-info-recurring-fieldset-BillingPeriod').val('OneTime');
		}
	});
	$('#piryx-donation-form input.required, #piryx-donation-form select.required').blur(function(){
		if($("#piryx-donation-form").validationEngine('validateField', $(this))){
			$(this).parent().addClass('errorItem').fadeTo(3000, 1, function(){
				$(this).find('.required').validationEngine('hidePrompt');
			});
		}
		else{ $(this).parent().removeClass('errorItem'); }
	});
	$('#piryx-donation-form #edit-fundraiser-fieldset-fundraiser-know').click(
		function(){ 
			if($('#piryx-donation-form #edit-fundraiser-fieldset-fundraiser-know:checked').val() == 1){
				$('#edit-fundraiser-fieldset-FundraiserID-wrapper, #edit-fundraiser-fieldset-SourceCode-wrapper').slideDown('normal'); 
			}
			else{ $('#edit-fundraiser-fieldset-FundraiserID-wrapper, #edit-fundraiser-fieldset-SourceCode-wrapper').slideUp('normal'); }
		}
	);
	
	$('#donation-amount-form-data input:radio').click(
		function(){
			$('#other-amount input').val("0");
		}
	);
	$('#other-amount input').blur(
		function(){
			if(($(this).val() != "0")&&($(this).val() != "")){ 
				$('#donation-amount-form-data input:radio').removeAttr("checked");
			}
			if($("#piryx-donation-form").validationEngine('validateField', $(this))){
				$(this).parent().addClass('errorItem').fadeTo(3000, 1, function(){
					$(this).find('input').validationEngine('hidePrompt');
				});
			}
			else{ $(this).parent().removeClass('errorItem'); }
		}
	);
	
	
	$('#piryx-donation-form').submit(function(){return donateValidate();});
	
});

function donateValidate(){
	var validate = true;
	$('#piryx-donation-form input.required, #piryx-donation-form select.required').each(function(){
		if($("#piryx-donation-form").validationEngine('validateField', $(this))){
			$(this).parent().addClass('errorItem');
			validate = false;
		}
	});
  
  if(validate == true) {
    $('.process-text').show();    
    $('#edit-submit').hide();
  }
  
	return validate;
};
