(function() {	jQuery.widgetSlider = function(settings) {				                settings = jQuery.extend({         viewport: "#widget-slider-viewport",         content: "#widget-slider-content",         next: "#widget-slider-next",         prev: "#widget-slider-prev",         item: "div",         direction: "vertical",         showAmount: 3        }, settings);                        if ( jQuery(settings.viewport).size() > 0 )        {        	        	sliderInfo = new Object();        	        	        	jQuery(settings.next).hide().attr("href","javascript:void(0)");        	jQuery(settings.prev).hide().attr("href","javascript:void(0)");        	if( jQuery(settings.item).size() > settings.showAmount )        	{ jQuery(settings.next).show(); }                	if( settings.direction == "vertical" )        	{        		        		sliderInfo.start = 0;        		sliderInfo.end = (jQuery(settings.item).size() - settings.showAmount) * jQuery(settings.item).eq(0).height() * -1;        		sliderInfo.itemSize = jQuery(settings.item).eq(0).height();        		sliderInfo.top = 0;        	}        	else        	{         		        		sliderInfo.start = 0;        		sliderInfo.end = (jQuery(settings.item).size() - settings.showAmount) * jQuery(settings.item).eq(0).width() * -1;        		sliderInfo.itemSize = jQuery(settings.item).eq(0).width();        		sliderInfo.left = 0;        	}        	        	        	prevButton(settings.prev,settings);        	nextButton(settings.next,settings);        }    };        prevButton = function(prevObj,settings) {    	$(prevObj).unbind("click");    	setTimeout(function() {     		$(prevObj).click(function() {    			         		if( settings.direction == "vertical")         		{         			sliderInfo.top = parseInt(jQuery(settings.content).css("top").split("px")[0]) + sliderInfo.itemSize;         			jQuery(settings.content).animate( { top:sliderInfo.top+"px"}, 350 );         		         			if( sliderInfo.top != sliderInfo.end )         			{ jQuery(settings.next).show(); }         			if( sliderInfo.top == 0 )         			{ jQuery(settings.prev).hide();  }         		}         		else         		{         			sliderInfo.left = parseInt(jQuery(settings.content).css("left").split("px")[0]) + sliderInfo.itemSize;         			jQuery(settings.content).animate( { left:sliderInfo.left+"px"}, 350 );         		         			if( sliderInfo.left != sliderInfo.end )         			{ jQuery(settings.next).show(); }         			if( sliderInfo.left == 0 )         			{ jQuery(settings.prev).hide();  }         		}         		         		prevButton(prevObj,settings);         	});        }, 351);    };        nextButton = function(nextObj,settings) {    	$(nextObj).unbind("click");    	setTimeout(function() {     		$(nextObj).click(function() {    			if(settings.direction == "vertical")         		{         			sliderInfo.top = parseInt(jQuery(settings.content).css("top").split("px")[0]) - sliderInfo.itemSize;         			jQuery(settings.content).animate( { top:sliderInfo.top+"px"}, 350 );         			         			if( sliderInfo.top == sliderInfo.end )         			{ jQuery(settings.next).hide(); }         			if( sliderInfo.top != 0 )         			{ jQuery(settings.prev).show();  }         		         		}         		else         		{         			sliderInfo.left = parseInt(jQuery(settings.content).css("left").split("px")[0]) - sliderInfo.itemSize;         			jQuery(settings.content).animate( { left:sliderInfo.left+"px"}, 350 );         		         			if( sliderInfo.left == sliderInfo.end )         			{ jQuery(settings.next).hide(); }         			if( sliderInfo.left != 0 )         			{ jQuery(settings.prev).show();  }         		}         	         		nextButton(nextObj,settings);         	});        }, 360);    };    })(jQuery);