(function($) {
    var isMobile = (navigator.userAgent.match(/(?:iPhone|iPad|iPod|Android)/i) !== null);
    var container = '#container';
    var footHeight = 0;
    var footHeightTotal = 0;
    var footMinHeight = 24;
    var wrapperBotPad = 0;
    var wrapperMinPad = 80;
    var footMain = '.footer-main';
    var footContent = '.footer-main .footer-cols';
    var ZyngaFooterLocked = false;
    var windowWidth = $(window).width();
    var breakpoints = {
        desktop: 1200,
        netbook: 979,
        tablet: 770,
        mobile: 480
    };

    var headCssObj = {
        'background-image': "url('/img/main-nav/bg-menu-top.png')",
        'background-position' : '-2px top',
        'background-repeat' : 'no-repeat'
    }
    
    var headAddCssObj = {
        'background-image': "url('/img/main-nav/add/bg-menu-top.png')",
        'background-position' : '-7px top',
        'background-repeat' : 'no-repeat'
    }

    var ZyngaHeader = {
        
        init: function() {
            ZyngaHeader.headerHover();
            ZyngaHeader.showHideZbar();
            ZyngaHeader.showHideAddMenu();
        },
        showHideAddMenu: function() {
           if( $(window).width() > breakpoints.netbook ){
	            $(window).scroll(function() {
	                if( $(window).scrollTop() >= 225 ){
	                    $('#additionalNav').show();
	                } else {
	                    if( $(window).width() > breakpoints.netbook ){
	                        $('#additionalNav').hide();
	                    }
	                }
	            });
            }
        },
        showHideZbar: function() {
            
            $(".nav-games-link a, #addPlayNow").click(function(e) {
                e.preventDefault();
                $('html,body').animate({
                    scrollTop: 0
                }, 600);
            });
        },
        headerHover: function(){
            $('#primary #superfish-1 a.menuparent').parent().hover(
                function(){
                    $(this).css(headCssObj);
                },
                function(){
                    $(this).css('background-image','none');
                }
                );
            $('#addMainMenu #superfish-2 a.menuparent').parent().hover(
                function(){
                    $(this).css(headAddCssObj);
                },
                function(){
                    $(this).css('background-image','none');
                }
            );
        }
        
    };

    var ZyngaFooter = {
        init: function() {
            footHeightTotal = $(footMain).height();
            footHeight = $(footContent).height();
            //if (isMobile) {
            //    footMain.css('position','relative');
            //    $('#wrapper').css('padding-bottom','64px');
            //} else {
            $(footContent).css('height',footMinHeight+'px');
            wrapperBotPad = footHeightTotal + wrapperMinPad;
            $('#wrapper').css('padding-bottom',wrapperBotPad+'px');
            ZyngaFooter.resizeFooter();
            //}
        
            $(footMain).hover( function(){
                $(footContent).css( 'height', footHeightTotal );
            }, function(){
                ZyngaFooter.resizeFooter();
            });
        },

        resizeFooter: function() {	
            //if(isMobile) return;
            var scrollTopOffset = $(document).height() - $(window).height();
            if (($(window).scrollTop() >= scrollTopOffset - (footHeight-footMinHeight-wrapperMinPad-wrapperMinPad)) && (!ZyngaFooterLocked)) {
                var scrollTopDiff = (scrollTopOffset - $(window).scrollTop());
                $(footContent).css({ 
                    'height': (footHeight-scrollTopDiff)+'px' 
                });
                $('#footer-logo').css('background-position','left -22px');
            } else {
                $(footContent).css({ 
                    'height': footMinHeight+'px' 
                });
                $('#footer-logo').css('background-position','top left');
            }
        },
        
        minimizeFooter: function() {
            //if(isMobile) return;
            $(footContent).css({ 
                'height': footMinHeight+'px' 
            });
        }

    };
    
    var ZyngaIsotope = {

        init: function() {
            
            // back to top button
            $('.btn-to-top').smoothScroll();
            
            // initial styles
            if( $('.pager').length > 0 && windowWidth > breakpoints.mobile ){
                $('.pager').css('visibility','hidden');
                ZyngaFooterLocked = true;
            }
            
            $('#zyne-search-field').removeAttr('disabled');
            $('#zyne-search-submit').removeAttr('disabled');
            
            // share handler
            $(container).delegate('.share', 'mouseenter', function(){
                $(this).addClass('share-hover');
                $(this).stop(true).animate({
                    height: '93px'
                }, 200, function() {
                    });
            });
            $(container).delegate('.share', 'mouseleave', function(){
                $(this).stop(true).animate({
                    height: '43px'
                }, 200, function() {
                    $(this).removeClass('share-hover');
                });
            });
            
            // search handler
            $('#zyne-search-form').submit( function(){
                ZyngaIsotope.zyneSearch();
            });
            
            $('.zyne-search-destroy').click( function(){
                $('.zyne-search-result').remove();
                $(container).isotope('reloadItems').isotope({
                    sortBy: 'original-order'
                });
            } );

        },
        
        initLoad: function() {
            
            // Isotope
            $(container).isotope({ 
                itemSelector: '.zyne-item',
                masonry: {
                    columnWidth: 250
                }
            }, function() {
                $('.zyne-item .post .share-social').css('visibility','visible');
                $('.zyne-item .post .share').css('visibility','visible');

            });
            
            if( windowWidth > breakpoints.mobile ){
                // Infinite Scroll
                $(container).infinitescroll({
                    loading: {
                        finishedMsg: "<em>No more pages to load ...</em>",
                        img: "img/zyne/globals/ajax-loader-2.gif",
                        msgText: "<em>Loading the next set of posts...</em>",
                        speed: 'fast'
                    },
                    state: {
                        currPage: 0
                    },
                    navSelector: '.pager',
                    nextSelector: '.pager-next a',
                    itemSelector: '.zyne-item',
                    bufferPx: 20,
                    errorCallback: function() {
                        ZyngaFooterLocked = false;
                        ZyngaFooter.resizeFooter();
                    },
                    animate: false
                },
                function( newElements ) {
                    var opts = $( container ).data( 'infinitescroll' ).options;
    
                    var $newElems = $( newElements ).css({
                        opacity: 0
                    });
                    $newElems.imagesLoaded(function() {
                        $newElems.css({
                            opacity: 1
                        });
                        $( container ).isotope( 'appended', $newElems, ZyngaIsotope.setFilter() );
                        $('.zyne-item .post .share-social').css('visibility','visible');
                        $('.zyne-item .post .share').css('visibility','visible');
    
                        //console.log("curr page: ", opts.state.currPage);
                        //console.log("isDone: ", opts.state.isDone);
                        //console.log("Item count: ", $('.zyne-item').length );
                            
                        if ( opts.state.currPage >= 3 ) {
                            $(container).infinitescroll('pause');
                            ZyngaFooterLocked = false;
                            
                            if( opts.state.isDone !== true ){
                                $('.zyne-load').show();
                            }
                        }
                    });
                });
    
                $('.zyne-filter').click(function(e) {
                    e.preventDefault();
                    if (!$(this).hasClass('sel')) {
                        $('.zyne-filter').removeClass('sel');
                        $(this).addClass('sel');
                        ZyngaIsotope.setFilter();
                    }
                });
    
                // load more handler
                $('.btn-load-more').click(function(e) {
                    e.preventDefault();
                    ZyngaFooter.minimizeFooter();
                    $(container).infinitescroll('retrieve');
                });
                
            }
                
        },
            
        setFilter: function() {
            
            $(container).imagesLoaded(function() {
                var filters = [];
                $('.zyne-filter.sel').each(function() {
                    var self = this;
                    if ($(self).attr('data-filter') === '.all') {
                        $('.zyne-filter').each(function() {
                            filters.push($(this).attr('data-filter'));
                        });
                    } else {
                        filters.push($(self).attr('data-filter'));
                    }
                });
                if (filters.length < 1) {
                    filter = '*';
                } else {
                    filter = filters.join(', ');
                }
                $(container).isotope({
                    filter: filter
                }, function() {
                    // show social stuff
                    $('.zyne-item .post .share-social').css('visibility','visible');
                    $('.zyne-item .post .share').css('visibility','visible');
                });
            });
       
            
        },
        
        zyneSearch: function() {
            if( !$('#zyne-search-controls').hasClass('search-locked') ){
                $('.zyne-search-message').remove();
                if ( $('#zyne-search-field').val().length > 3 ) {

                    $('#zyne-search-field').attr('disabled', 'disabled');
                    $('#zyne-search-submit').attr('disabled', 'disabled').attr('src', '/img/zyne/zyne-search-loader.gif');
                    
                    $.get('zynga_zyne/search/' + $('#zyne-search-field').val(),
                        function( response ){
                            if( response.nids.length > 1 ){

                                $('#zyne-search-controls').addClass('search-locked');

                                var nids = response.nids;
                                for( var i=0; i < nids.length; i++) {
                                    if( document.getElementById( nids[i] ) ) {
                                        $( '#' + nids[i] ).remove();
                                    }
                                }

                                var body = response.body;
                                $(container).prepend(body);
                                    
                                $('.zyne-item .post .share-social').css('visibility','visible');
                                $('.zyne-item .post .share').css('visibility','visible');
                                    
                                for( var j=0; j < nids.length; j++) {
                                    $('#' + nids[j] ).addClass( 'zyne-search-result zyne-search-current' );
                                }

                                $('#zyne-search-submit').removeAttr('disabled').attr('src', '/img/zyne/zyne_search_cancel.png');
                                    
                                $(container).imagesLoaded( function(){
                                    $(container).isotope('reloadItems').isotope({
                                        sortBy: 'original-order'
                                    });
                                });

                            } else {
                                $('.col-l-header').append('<div class="zyne-search-message">No Results :(</div>');
                                    
                                $('#zyne-search-field').removeAttr('disabled');
                                $('#zyne-search-submit').removeAttr('disabled').attr('src', '/img/zyne/zyne_feed_search_icon.png');
                            }
                        }, 'json'
                        );

                } else {
                    $('.col-l-header').append('<div class="zyne-search-message">Plz &gt; 3 chars</div>');
                }
            } else {
                $(container).children().removeClass('zyne-search-current');
                $('#zyne-search-field').removeAttr('disabled').val('');
                $('#zyne-search-submit').removeAttr('disabled').attr('src', '/img/zyne/zyne_feed_search_icon.png');
                $('#zyne-search-controls').removeClass('search-locked');
            }
        }
    
    };

    window.ZyngaFooter = ZyngaFooter;
    window.ZyngaHeader = ZyngaHeader;
    window.ZyngaIsotope = ZyngaIsotope;
    
    $(document).ready(function() {
        if(isMobile) {
            $("#privacy, #privacyTitle").hide();
            $("#seeFull").click(function(){
                $("#shortNotice").hide();
                $("#privacyTitle, #privacy").show();
            });
        }
        if( windowWidth > breakpoints.netbook && $( 'body' ).hasClass( 'front' ) ){ 
            ZyngaFooter.init();
            ZyngaHeader.init();
            $(window).resize(function() {
                ZyngaFooter.resizeFooter();
            });
            $(window).scroll(function() {
                ZyngaFooter.resizeFooter();
            });
            $('.footer-main').css('position','fixed');
        }
        
        if( $( 'body' ).hasClass( 'front' ) ){
            ZyngaIsotope.init();
            $(window).load(function() {
                ZyngaIsotope.initLoad();
            });
            $('.pane-home-feature-carousel .views-slideshow-controls-top a').mousedown(function() {
                $(this).css('background-position','top left');
            });
            $('.pane-home-feature-carousel .views-slideshow-controls-top a').mouseup(function() {
                $(this).css('background-position','left -73px');
            });
            $('.pane-home-feature-carousel .views-slideshow-controls-top a').hover(function() {
                $(this).css('background-position', 'left -73px');
            },
            function() {
                $(this).css('background-position', 'top left')
            });
        }
        
    });

})(jQuery);;
